type RealmORM struct {
	CoreId    *string
	CreatedAt *time.Time
	Id        string `gorm:"type:uuid;primaryKey"`
	Key       string
	UpdatedAt *time.Time
	Value     string
}

// TableName overrides the default tablename generated by GORM
func (RealmORM) TableName() string {
	return "realms"
}

// ToORM runs the BeforeToORM hook if present, converts the fields of this
// object to ORM format, runs the AfterToORM hook, then returns the ORM object
func (m *Realm) ToORM(ctx context.Context) (RealmORM, error) {
	to := RealmORM{}
	var err error
	if prehook, ok := interface{}(m).(RealmWithBeforeToORM); ok {
		if err = prehook.BeforeToORM(ctx, &to); err != nil {
			return to, err
		}
	}
	to.Id = m.Id
	if m.CreatedAt != nil {
		t := m.CreatedAt.AsTime()
		to.CreatedAt = &t
	}
	if m.UpdatedAt != nil {
		t := m.UpdatedAt.AsTime()
		to.UpdatedAt = &t
	}
	to.Key = m.Key
	to.Value = m.Value
	if posthook, ok := interface{}(m).(RealmWithAfterToORM); ok {
		err = posthook.AfterToORM(ctx, &to)
	}
	return to, err
}

// ToPB runs the BeforeToPB hook if present, converts the fields of this
// object to PB format, runs the AfterToPB hook, then returns the PB object
func (m *RealmORM) ToPB(ctx context.Context) (Realm, error) {
	to := Realm{}
	var err error
	if prehook, ok := interface{}(m).(RealmWithBeforeToPB); ok {
		if err = prehook.BeforeToPB(ctx, &to); err != nil {
			return to, err
		}
	}
	to.Id = m.Id
	if m.CreatedAt != nil {
		to.CreatedAt = timestamppb.New(*m.CreatedAt)
	}
	if m.UpdatedAt != nil {
		to.UpdatedAt = timestamppb.New(*m.UpdatedAt)
	}
	to.Key = m.Key
	to.Value = m.Value
	if posthook, ok := interface{}(m).(RealmWithAfterToPB); ok {
		err = posthook.AfterToPB(ctx, &to)
	}
	return to, err
}

// The following are interfaces you can implement for special behavior during ORM/PB conversions
// of type Realm the arg will be the target, the caller the one being converted from

// RealmBeforeToORM called before default ToORM code
type RealmWithBeforeToORM interface {
	BeforeToORM(context.Context, *RealmORM) error
}

// RealmAfterToORM called after default ToORM code
type RealmWithAfterToORM interface {
	AfterToORM(context.Context, *RealmORM) error
}

// RealmBeforeToPB called before default ToPB code
type RealmWithBeforeToPB interface {
	BeforeToPB(context.Context, *Realm) error
}

// RealmAfterToPB called after default ToPB code
type RealmWithAfterToPB interface {
	AfterToPB(context.Context, *Realm) error
}

// DefaultCreateRealm executes a basic gorm create call
func DefaultCreateRealm(ctx context.Context, in *Realm, db *gorm.DB) (*Realm, error) {
	if in == nil {
		return nil, errors.NilArgumentError
	}
	ormObj, err := in.ToORM(ctx)
	if err != nil {
		return nil, err
	}
	if hook, ok := interface{}(&ormObj).(RealmORMWithBeforeCreate_); ok {
		if db, err = hook.BeforeCreate_(ctx, db); err != nil {
			return nil, err
		}
	}
	if err = db.Omit().Create(&ormObj).Error; err != nil {
		return nil, err
	}
	if hook, ok := interface{}(&ormObj).(RealmORMWithAfterCreate_); ok {
		if err = hook.AfterCreate_(ctx, db); err != nil {
			return nil, err
		}
	}
	pbResponse, err := ormObj.ToPB(ctx)
	return &pbResponse, err
}

type RealmORMWithBeforeCreate_ interface {
	BeforeCreate_(context.Context, *gorm.DB) (*gorm.DB, error)
}
type RealmORMWithAfterCreate_ interface {
	AfterCreate_(context.Context, *gorm.DB) error
}

func DefaultReadRealm(ctx context.Context, in *Realm, db *gorm.DB) (*Realm, error) {
	if in == nil {
		return nil, errors.NilArgumentError
	}
	ormObj, err := in.ToORM(ctx)
	if err != nil {
		return nil, err
	}
	if ormObj.Id == "" {
		return nil, errors.EmptyIdError
	}
	if hook, ok := interface{}(&ormObj).(RealmORMWithBeforeReadApplyQuery); ok {
		if db, err = hook.BeforeReadApplyQuery(ctx, db); err != nil {
			return nil, err
		}
	}
	if hook, ok := interface{}(&ormObj).(RealmORMWithBeforeReadFind); ok {
		if db, err = hook.BeforeReadFind(ctx, db); err != nil {
			return nil, err
		}
	}
	ormResponse := RealmORM{}
	if err = db.Where(&ormObj).First(&ormResponse).Error; err != nil {
		return nil, err
	}
	if hook, ok := interface{}(&ormResponse).(RealmORMWithAfterReadFind); ok {
		if err = hook.AfterReadFind(ctx, db); err != nil {
			return nil, err
		}
	}
	pbResponse, err := ormResponse.ToPB(ctx)
	return &pbResponse, err
}

type RealmORMWithBeforeReadApplyQuery interface {
	BeforeReadApplyQuery(context.Context, *gorm.DB) (*gorm.DB, error)
}
type RealmORMWithBeforeReadFind interface {
	BeforeReadFind(context.Context, *gorm.DB) (*gorm.DB, error)
}
type RealmORMWithAfterReadFind interface {
	AfterReadFind(context.Context, *gorm.DB) error
}

