package host

import (
	"github.com/gofrs/uuid"
	"gorm.io/gorm"
)

// BeforeCreate - GORM-specific autogenerated helpers.
func (filesystem *FileSystemORM) BeforeCreate(tx *gorm.DB) (err error) {
	id, err := uuid.NewV4()
	if err != nil {
		return err
	}
	filesystem.Id = id.String()

	return nil
}

// BeforeCreate - GORM-specific autogenerated helpers.
func (file *FileORM) BeforeCreate(tx *gorm.DB) (err error) {
	id, err := uuid.NewV4()
	if err != nil {
		return err
	}
	file.Id = id.String()

	return nil
}
